import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 3, description: "Purse", quantity: 22, packed: true },
];

function App() {
  const[items, setItems] = useState([]);

  function handleAddItems(item){
    setItem((items)=>[...items,item])
  }
  console.log(items)


  return (
    <>
      <Logo/>
      <Form onadHandler={handleAddItems} />
      <PackingList item ={items}  />
      <Stats/>
    </>
  );
}

export default App;

function Logo(){
  return(
    <h1>üå¥üéãFAR AWAYüéãüéã</h1>
  )
}

function Form({onaddHandler}){
  const[desc, setDesc] = useState("")
  const[dropdownvalue, setDropdownvalue] = useState(1);




function submitHandler(e){
e.preventDefault();
if(desc==="") return;
const newItem = {desc,dropdownvalue,packed:false,id:Date.now}

onaddHandler(newItem);
// console.log(item)
setDesc("");
setDropdownvalue(1)

}

   //Number is used to convert it into number in return part
  return (
    <form className="add-form" onSubmit={submitHandler} >
      <h3> What do you need for your trip ‚ù§Ô∏è</h3>
      <select value={dropdownvalue} onChange={(e)=>{setDropdownvalue(Number(e.target.value))}}>

        {Array.from({length:20},(_,i)=>i+1).map((el)=><option value={el} key={el}>{el}</option>)}
      </select>
      <input type="text" placeholder="items..." value={desc} onChange={(e)=>{setDesc(e.target.value)}} />
      <button>ADD</button>
    </form>
  )
}

function PackingList({item}){
  return(
    <div className="list">
      <ul> 
      {item.map((e)=>
        <Items item ={e} key={e.id}/>
      )}
      </ul>
    </div>
  )
}

function Items({item}){
  return(
    <li style={item.packed?{textDecoration:'line-through'}:{}}>
      {item.quantity} {item.description}
      <span><button>‚ùå</button></span></li>
  )
}

function Stats(){
  return(
    <div className="stats">Stats</div>
  )
}
