import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 3, description: "Purse", quantity: 22, packed: false },
];

function App() {
  const[items, setItems] = useState(initialItems);

  function handleToggleItem(id){
    setItems((items)=>items.map((item)=>item.id === id ? {...item,packed:!item.packed} : item))
  }

  function handleAddItems(item){
    setItems((items)=>[...items,item])
  }
  console.log(items)

  function handleDeleteItem(id){
    setItems((items)=>items.filter((item) => (item.id) !==id))
  }

  function handleClearList(){
    alert("ARE YOU REALLY WANT TO DELETE ALL ITEMS ? ")
    setItems([])
  }


  return (
    <>
      <Logo/>
      <Form onaddHandler={handleAddItems} />
      <PackingList item ={items} onDeleteItem={handleDeleteItem} onToggleItem = {handleToggleItem} onClearList = {handleClearList}/>
      <Stats items={items}/>
    </>
  );
}

export default App;

function Logo(){
  return(
    <h1>üå¥üéãFAR AWAYüéãüéã</h1>
  )
}

function Form({onaddHandler}){
  const[description, setDescription] = useState("")
  const[quantity, setQuantity] = useState(1);




function submitHandler(e){
e.preventDefault();
if(description==="") return;
const newItem = {description,quantity,packed:false,id:Date.now()}

onaddHandler(newItem);
// console.log(item)
setDescription("");
setQuantity(1)

}

   //Number is used to convert it into number in return part
  return (
    <form className="add-form" onSubmit={submitHandler} >
      <h3> What do you need for your trip ‚ù§Ô∏è</h3>
      <select value={quantity} onChange={(e)=>{setQuantity(Number(e.target.value))}}>

        {Array.from({length:20},(_,i)=>i+1).map((el)=><option value={el} key={el}>{el}</option>)}
      </select>
      <input type="text" placeholder="items..." value={description} onChange={(e)=>{setDescription(e.target.value)}} />
      <button>ADD</button>
    </form>
  )
}

function PackingList({item, onDeleteItem, onToggleItem,onClearList}){

  const[sortBy, setSortBy] = useState("input")
  let SortedItems;

  if(sortBy === "clear") SortedItems = [];

  if(sortBy === "input") SortedItems = item;

  if(sortBy === "description") 
  {
    SortedItems = item.slice().sort((a,b) => a.description.localeCompare(b.description))
  }

  if(sortBy === "packed") 
  {
    SortedItems = item.slice().sort((a,b) => Number(a.packed) - Number(b.packed))
  }

  return(
    <div className="list">
      <ul> 
      {SortedItems.map((e)=>
        <Items item ={e} key={e.id} onDeleteItem={onDeleteItem} onToggleItem={onToggleItem}/>
      )}
      </ul>
      <div className="actions">
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="input">Order by Input</option>
          <option value="description">Order by Description</option>
          <option value="packed">Order by Packed Status</option>
        </select>
        <span><button onClick={onClearList}>Clear All</button></span>
      </div>
    </div>
  )
}

function Items({item,onDeleteItem, onToggleItem}){
  return(
    <li style={item.packed?{textDecoration:'line-through'}:{}}>
      <input type="checkbox" value={item.packed} onChange={() => onToggleItem(item.id)}/>
      {item.quantity} {item.description}
      <span><button onClick={()=>onDeleteItem(item.id)}>‚ùå</button></span></li>
  )
}

function Stats({items}){
  if(items.length === 0)
    return(
    <div className="stats">
      <p><em>Start adding Items in you list</em></p>
    </div>
    )

  const totalItem= items.length;
  const packedItem= (items.filter(item => item.packed)).length
  const percentPacked= Math.round(packedItem/totalItem*100)
  return(
    <div className="stats">{ percentPacked === 100 ?
       "You got everything! Ready to go ‚úàÔ∏è " :
       ` You Have ${totalItem} item in your List and you already Packed ${packedItem} ( ${percentPacked} %)`}</div>

  )
}
