const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 2, description: "Purse", quantity: 22, packed: true },
];

function App() {


  return (
    <>
      <Logo/>
      <Form/>
      <PackingList />
      <Stats/>
    </>
  );
}

export default App;

function Logo(){
  return(
    <h1>üå¥üéãFAR AWAYüéãüéã</h1>
  )
}

function Form(){
  return (
    <div className="add-form">
      <h3> What do you need for your trip ‚ù§Ô∏è</h3>
      <select>
        {Array.from({length:20},(_,i)=>i+1).
        map((el)=><option value={el} key={el}>{el}</option>)}
      </select>
      <input type="text" placeholder="items..." />
      <button>ADD</button>
    </div>
  )
}

function PackingList(){
  return(
    <div className="list">
      <ul> 
      {initialItems.map((e)=>
        <Items item ={e}/>
      )}
      </ul>
    </div>
  )
}

function Items({item}){
  return(
    <li style={item.packed?{textDecoration:'line-through'}:{}}>
      {item.quantity} {item.description}
      <button>‚ùå</button></li>
  )
}

function Stats(){
  return(
    <div className="stats">Stats</div>
  )
}
